import React from "react";

const TechStack = () => (
  <section className="relative py-20 bg-bg-primary overflow-hidden">
    {/* Gradient Background */}
    <div className="absolute top-0 left-0 w-full h-full">
      <div className="absolute top-1/4 right-1/4 w-[500px] h-[500px] bg-violet-600/20 rounded-full blur-[120px]" />
      <div className="absolute bottom-1/4 left-1/4 w-[500px] h-[500px] bg-indigo-600/20 rounded-full blur-[120px]" />
    </div>

    <div className="container mx-auto px-8 relative z-10">
      <h2 className="text-4xl font-bold mb-16 text-center text-text-primary">
        Technologies I <span className="text-accent">work with</span>
      </h2>
      
      <div className="grid grid-cols-3 md:grid-cols-4 gap-12 max-w-4xl mx-auto">
        {[
          'figma', 'react', 'node', 'js', 'css',
          'xd', 'next', 'mongodb'
        ].map((tech) => (
          <div 
            key={tech}
            className="group relative flex items-center justify-center"
          >
            <div className="absolute inset-0 bg-accent/10 rounded-full blur-xl group-hover:bg-accent/30 transition-colors" />
            <div className="w-16 h-16 rounded-full bg-bg-secondary p-3 backdrop-blur-sm border border-text-primary/10 transition-transform group-hover:scale-110">
              <img
                src={`/icons/${tech}.svg`}
                alt={tech}
                className="w-full h-full object-contain"
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  </section>
);

export default TechStack;
