import React, { useState, useEffect } from "react";

const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToElement = (elementId) => {
    const element = document.getElementById(elementId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <header className={`fixed w-full top-0 z-50 transition-all duration-300 ${
      isScrolled ? 'bg-bg-secondary/90 backdrop-blur-md shadow-lg' : 'bg-transparent'
    }`}>
      <nav className="container mx-auto flex justify-between items-center py-6 px-8">
        <span className="text-3xl font-bold text-text-primary hover:text-accent transition-colors">
          SARXNNN
        </span>
        <ul className="flex gap-12">
          {[
            ['home', 'Home'],
            ['about', 'About'],
            ['skills', 'Skills'],
            ['projects', 'Projects'],
            ['contact', 'Contact'],
          ].map(([id, label]) => (
            <li key={id}>
              <button
                onClick={() => scrollToElement(id)}
                className="text-text-primary hover:text-accent transition-colors relative group"
              >
                {label}
                <span className="absolute -bottom-1 left-0 w-0 h-0.5 bg-accent transition-all group-hover:w-full" />
              </button>
            </li>
          ))}
        </ul>
      </nav>
    </header>
  );
};

export default Header;
