import React from "react";

const About = () => (
  <section id="about" className="container mx-auto py-16 px-6">
    <h2 className="text-3xl font-bold mb-4 text-gray-900">About Me</h2>
    <p className="text-lg text-gray-700 max-w-2xl">
      {/* Replace with your own bio */}
      I am a passionate frontend developer with experience in React, Tailwind CSS, and modern web technologies. I love building beautiful and functional user interfaces.
    </p>
  </section>
);

export default About;
