import React from "react";

const projects = [
  {
    title: "Project One",
    description: "A short description of project one.",
    image: "https://placehold.co/400x240",
    link: "#"
  },
  {
    title: "Project Two",
    description: "A short description of project two.",
    image: "https://placehold.co/400x240",
    link: "#"
  },
  {
    title: "Project Three",
    description: "A short description of project three.",
    image: "https://placehold.co/400x240",
    link: "#"
  }
];

const Projects = () => (
  <section id="projects" className="container mx-auto py-16 px-6">
    <h2 className="text-3xl font-bold mb-8 text-gray-900">Projects</h2>
    <div className="grid md:grid-cols-3 gap-8">
      {projects.map((project, idx) => (
        <div key={idx} className="bg-white rounded-lg shadow p-4 flex flex-col">
          <img src={project.image} alt={project.title} className="rounded mb-4" />
          <h3 className="text-xl font-semibold mb-2">{project.title}</h3>
          <p className="text-gray-600 mb-4">{project.description}</p>
          <a
            href={project.link}
            className="mt-auto text-blue-600 hover:underline font-medium"
            target="_blank" rel="noopener noreferrer"
          >
            View Project
          </a>
        </div>
      ))}
    </div>
  </section>
);

export default Projects;
